import axios from "axios";
import { useEffect, useState } from "react";
import Header from "./Header";
import { useNavigate } from "react-router-dom";
function Accounts(){
    const navigate=useNavigate()
    const [accounts,setAccounts]=useState([])
    const [data,setData]=useState({
        "found":false,
        "accno":""
    })
    const [tran,setTran]=useState({
        "accno":"",
       
    })
    
    const handleInput=(e)=>{
        setTran({...tran,[e.target.name]:e.target.value})
    }

    useEffect(()=>{
        axios.get("http://localhost:8090/api/accounts")
        .then(resp=>{
            setAccounts(resp.data.data)
            console.log(accounts)
        })
    },[])
    const handleSubmit=e=>{
        e.preventDefault()
        tran.accno=data.accno
            console.log(tran)
            axios.post("http://localhost:8090/api/accounts/deleteaccount/{tran.accno}",tran)
            .then(resp=>{
                console.log(resp.data)
                navigate('/accounts')
            })
            .catch(err=>{
                console.log(err)
            })
    }
    
    return(
        <>
        <Header/>
        <div className="container-fluid">
            <div className="row">
                <div style={{paddingLeft:"210px"}}>
                    <h4 className="text-left p-2 border-bottom border-success"></h4>
                    <table className="table ">
                <thead className="thead-light">
                    <tr>
                        <th>Account No</th>
                        <th>Holder Name</th>
                        <th>Holder Address</th>
                        <th>Account Type</th>                        
                        <th>Balance</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                {accounts.map(x=>(
                    <tr key={x.accno}>
                        <td>{x.accno}</td>
                        <td>{x.customer.cname}</td>
                        <td>{x.customer.address}</td>
                        <td>{x.actype}</td>
                        <td>&#8377; {x.balance}</td>
                        <td>{x.active ? "Yes":"No"}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <form onSubmit={handleSubmit}>
            <label className="col-sm-2 col-form-label" style={{color:"white"}}>acc no</label>
                                <div className="col-3">
                                    <input type="number" min="1" name="accno" value={tran.accno} onChange={handleInput} className="form-control" />
                                </div>
            <button >Delete</button>
            </form>
            
                </div>
            </div>
        </div>
        </>
    )
}

export default Accounts;
