import axios from "axios";
import { useEffect, useState } from "react";
import Header from "./Header";
import SearchAccount from "./SearchAccount";
import SideBar from "./SideBar";

function Passbook(){
    const [trans,setTrans]=useState([])
    const [data,setData]=useState({
        "found":false,
        "accno":""
    })

    useEffect(()=>{
        console.log(data)
        axios.get("http://localhost:8090/api/accounts/passbook/"+data.accno)
        .then(resp=>{
            console.log(resp.data)    
            setTrans(resp.data.data)        
        })
        .catch(err=>{
            console.log(err)
        })
    },[data])

    return(
        <>
        <Header/>
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm-2 bg-transparent p-0 border-right border-primary" style={{height:"calc(100vh - 80px)"}}>
                    <SideBar />
                </div>
                <div className="col-sm-10">
                    <h4 className="text-left p-2 border-bottom border-success">Passbook</h4>
                    <SearchAccount setData={setData} />
                    {data.found ? (
                    <table class="table table-bordered table-striped table-sm">
						<thead class="table-primary">
							<tr>
								<th>Id</th>
								<th>Transaction Date</th>
								<th>Particulars</th>
								<th>Dr Amount</th>
								<th>Cr Amount</th>
							</tr>
						</thead>
						<tbody>
                            {trans.map(tran=>(
							<tr>
								<td>{tran.id }</td>
								<td>{tran.tdate }</td>
								<td>{tran.ttype }</td>
								<td>{tran.dramount==0 ? "" : tran.dramount}</td>
								<td>{tran.cramount==0 ? "":tran.cramount }</td>
							</tr>
                            ))}
						</tbody>
					</table>
                    ):""}
                </div>
            </div>
        </div>
        </>
    )
}

export default Passbook;
