import axios from "axios";
import { useEffect, useState } from "react";
import Header from "./Header";
import SideBar from "./SideBar";

function CustomerHistory(){
    const cid=sessionStorage.getItem("id")
    const [trans,setTrans]=useState([])
    useEffect(()=>{
        axios.get("http://localhost:8090/api/accounts/history/"+cid)
        .then(resp=>{
            console.log(resp.data)    
            setTrans(resp.data.data)        
        })
        .catch(err=>{
            console.log(err)
        })
    },[])

    return(
        <>
        <Header/>
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm-2 bg-transparent p-0 border-right border-primary" style={{height:"calc(100vh - 80px)"}}>
                    <SideBar />
                </div>
                <div className="col-sm-10">
                    <h4 className="text-left p-2 border-bottom border-success">Transactions History</h4>
                    <table class="table">
						<thead class="thead-light">
							<tr>
								<th>Id</th>
								<th>Transaction Date</th>
								<th>Particulars</th>
								<th>Dr Amount</th>
								<th>Cr Amount</th>
							</tr>
						</thead>
						<tbody>
                            {trans.map(tran=>(
							<tr>
								<td>{tran.id }</td>
								<td>{tran.tdate }</td>
								<td>{tran.ttype }</td>
								<td>{tran.dramount==0 ? "" : tran.dramount}</td>
								<td>{tran.cramount==0 ? "":tran.cramount }</td>
							</tr>
                            ))}
						</tbody>
					</table>
                </div>
            </div>
        </div>
        </>
    )
}

export default CustomerHistory;
